import React, { useState } from 'react'
import { InvestSumma } from "../chat-bot/InvestSumma"
import { LanSumma } from "../chat-bot/LanSumma"
import { RantaSumma } from "../chat-bot/RantaSumma"
import { Total } from "../chat-bot/Total"
import { VillaVarde } from "../chat-bot/VillaVarde"
import { Kampanj } from './Kampanj'

export const FormBot = () => {
    const [section, setSection] = useState("start")
    const kampanjoption = ["Ja", "Nej"]
    const summa = ["100000", "200000", "300000", "400000"]
    const [valdSumma, setValdSumma] = useState("")
    const [husLan, setHusLan] = useState("")
    const [ranta, setRanta] = useState("")
    const [villavarde, setVillaVarde] = useState("")
    const [kampanjval, setKampanjval] = useState("")


    const handleSubmit = event => {
        event.preventDefault();
    };


    return (
        <div>
            <form onSubmit={handleSubmit} >
                {section === "start" && (
                    <section className="section-div">
                        <p>Hejsan! Har du en villatomt?</p>
                        <button className="ja-knapp" type="submit" onClick={e => setSection("harTomt")}>Ja</button>
                        <button className="nej-knapp" type="submit" onClick={e => setSection("ingenTomt"), (5000)}>Nej</button>
                    </section>
                )}
                {section === "ingenTomt" && (
                    <div>
                        <p>Du behöver vara ägare av en tomt i Stockholm för att kunna bygga med oss</p>
                    </div>
                )}
                {section === "harTomt" && (
                    <div>
                        <p>
                            Fantastiskt! Då kan du tjäna 40.000kr per år!
                            </p>
                        <div>
                            <p>Kan du tänka dig korttidsuthyrning för att tjäna från 10000 kr extra per år?
</p>
                            <button className="ja-knapp" type="submit" onClick={e => setSection("jaKorttids")}>ja</button>
                            <button className="nej-knapp" type="submit" onClick={e => setSection("nejKorttids")}>Nej</button>
                        </div>
                    </div>
                )}
                {section === "nejKorttids" && (
                    <div>
                        <p>Hur mycket är din villa värderad till idag?</p>
                        <VillaVarde />
                        <p>Hur mycket uppgår ditt huslån till idag?</p>
                        <LanSumma />
                        <p>Okej! Vilken räntesats har du idag ungefär?</p>
                        <RantaSumma />
Vill du ta del av Attefallsförmedlarens kampanjränta och förbättra dina månadskostnader?
                        <Kampanj />
                        <button className="ja-knapp" type="submit" onClick={e => setSection("kalkyl")}>Gör en kalkyl</button>
                        {/* <button className="ja-knapp" type="submit" onClick={e => setSection("huslan")}>Räkna</button> */}
                    </div>
                )}
                {section === "kalkyl" && (
                    <div>
                        <Total ranta={ranta} husLan={husLan} />
                    </div>

                )}
                {section === "villInvestera" && (
                    <div>
                        <p>Toppen, ungfär hur mycket?</p>
                        <fieldset>
                            <button className="order-knapp" type="submit" disabled={valdSumma === ""} onClick={e => setSection("huslan")} >Välj summa</button>
                            <InvestSumma valdsumma={valdSumma} setValdSumma={setValdSumma} summa={summa} />
                        </fieldset>
                    </div>
                )}
                {section === "huslan" && (
                    <>
                        <div>
                            <p>Då kan du faktiskt tjäna så mycket som 80.000 kr per år med ett attefallshus från oss. Bra va!</p>
                        </div>
                        <div>
                            <p>Hur mycket kostar dina huslån idag per månad? </p>
                            <fieldset>
                                <legend>fyll i en summa</legend>
                                <LanSumma husLan={husLan} setHusLan={setHusLan} />
                                <button className="order-knapp" type="submit" disabled={husLan === ""} onClick={e => setSection("ranta")}>Välj summa</button>
                            </fieldset>
                        </div>
                    </>
                )}
                {section === "ranta" && (
                    <div>
                        <p>Okej! Vad har du för ränta? </p>
                        <fieldset id="submit">
                            <legend>fyll i % form. ex: 1.75</legend>
                            <RantaSumma ranta={ranta} setRanta={setRanta} />
                            <button className="order-knapp" type="submit" disabled={ranta === ""} onClick={e => setSection("samanfatta")}>Välj & räkna ut!</button>
                        </fieldset>
                    </div>
                )}
            </form>
            {section === "samanfatta" && (
                <article className="sammanfatta">
                    <Total ranta={ranta} husLan={husLan} />
                </article>
            )}
        </div>
    )
}
